import React, { useEffect, useState } from 'react';
import CandidateList from './components/CandidateList';

function App() {
  const [posts, setPosts] = useState([]);
  const [candidates, setCandidates] = useState([]);
  const [selected, setSelected] = useState(null);

  const BASE_URL = 'http://127.0.0.1:8000'; // Backend URL

  useEffect(() => {
    fetch(`${BASE_URL}/job_posts`)
      .then((res) => res.json())
      .then(setPosts)
      .catch(err => console.error("Error fetching job posts:", err));
  }, []);

  const loadRecs = (postId) => {
    setSelected(postId);
    fetch(`${BASE_URL}/recommend/${postId}`)
      .then((res) => res.json())
      .then(setCandidates)
      .catch(err => console.error("Error fetching recommendations:", err));
  };

  return (
    <div style={{ fontFamily: 'Arial', padding: 20 }}>
      <h2>DataSlush â€” Talent Recommender (PoC)</h2>
      <div style={{ display: 'flex', gap: 20 }}>
        {/* Job Posts Panel */}
        <div style={{ width: 300 }}>
          <h3>Job Posts</h3>
          {posts.map((p) => (
            <div
              key={p.id}
              style={{
                border: '1px solid #ddd',
                padding: 10,
                marginBottom: 10,
                background: selected === p.id ? '#f9f9f9' : 'white',
              }}
            >
              <b>{p.title}</b>
              <div style={{ fontSize: 12, marginTop: 6 }}>
                {p.description.slice(0, 120)}...
              </div>
              <button
                onClick={() => loadRecs(p.id)}
                style={{ marginTop: 6 }}
              >
                Recommend Top 10
              </button>
            </div>
          ))}
        </div>

        {/* Candidates Panel */}
        <div style={{ flex: 1 }}>
          <h3>Top Candidates</h3>
          {candidates.length === 0 && (
            <div>Select a post and click Recommend</div>
          )}
          <CandidateList candidates={candidates} />
        </div>
      </div>
    </div>
  );
}

export default App;
